mapper.update:
UPDATE CLUB_ATTACHMENT
SET ORIGINAL_NAME=#{originalName},
	CHANGE_NAME=#{changeName}
WHERE REF_CLUB_NO=#{clubNo} AND FILE_TYPE=#{fileType}

mapper.select:
SELECT A.*, B.*, C.REF_CLUB_NO, C.ORIGINAL_NAME, C.CHANGE_NAME, C.FILE_TYPE FROM CLUB A LEFT JOIN CLUB_TIME B ON A.CLUB_NO = B.REF_CLUB_NO LEFT JOIN CLUB_ATTACHMENT C ON A.CLUB_NO = C.REF_CLUB_NO WHERE A.STATUS='Y' AND A.CLUB_NO IN
#{item}
ORDER BY B.CLUB_DATE

mapper.update:
UPDATE CLUB
SET CONDITION=#{condition}
WHERE CLUB_NO=#{clubNo}

mapper.select:

SELECT COUNT(*) 
FROM CLUB 
WHERE STATUS='Y'
AND TO_CHAR(CSTART_DATE, 'YYYY-MM-DD') <= TO_CHAR(SYSDATE, 'YYYY-MM-DD') 
  
	
		
			AND CONDITION=#{type}
			AND TO_CHAR(CEND_DATE, 'YYYY-MM-DD') > TO_CHAR(SYSDATE, 'YYYY-MM-DD') 
		
		
			AND CONDITION=#{type}
			AND TO_CHAR(CEND_DATE, 'YYYY-MM-DD') < TO_CHAR(SYSDATE, 'YYYY-MM-DD') 
		
		
			AND CONDITION=#{type}
		
	


	AND CATEGORY=#{category}

SELECT A.* , B.CHANGE_NAME FROM CLUB A LEFT JOIN CLUB_ATTACHMENT B ON A.CLUB_NO = B.REF_CLUB_NO WHERE A.CONDITION=4 AND A.STATUS='Y' AND TO_CHAR(CSTART_DATE, 'YYYY-MM-DD') <= TO_CHAR(SYSDATE, 'YYYY-MM-DD') AND TO_CHAR(CEND_DATE, 'YYYY-MM-DD') > TO_CHAR(SYSDATE, 'YYYY-MM-DD')
AND CATEGORY=#{category}
AND B.FILE_TYPE=2 ORDER BY CSTART_DATE

SELECT COUNT(*) FROM CLUB A JOIN CLUB_ATTACHMENT B ON A.CLUB_NO = B.REF_CLUB_NO WHERE A.CLUB_NO IN (SELECT REF_CLUB_NO FROM CLUB_TIME
AND CLUB_DATE >= #{startDate}
AND CLUB_DATE <= #{endDate}
GROUP BY REF_CLUB_NO) AND A.CONDITION = 4 AND A.STATUS = 'Y' AND B.FILE_TYPE = 2
AND (CLUB_TITLE LIKE '%'||#{keyword}||'%' OR BK_NAME LIKE '%'||#{keyword}||'%')
AND ONOFF_LINE IN
#{item}
AND CATEGORY IN
#{item}
AND TIMES IN
#{item}
ORDER BY CLUB_NO

SELECT A.CLUB_NO, A.CLUB_TITLE, A.CATEGORY, A.ONOFF_LINE, A.HEART_COUNT, B.CHANGE_NAME FROM CLUB A JOIN CLUB_ATTACHMENT B ON A.CLUB_NO = B.REF_CLUB_NO WHERE STATUS='Y' AND A.CONDITION = 4 AND B.FILE_TYPE = 2 AND A.CLUB_NO IN (SELECT REF_CLUB_NO FROM CLUB_TIME
AND CLUB_DATE >= #{startDate}
AND CLUB_DATE <= #{endDate}
GROUP BY REF_CLUB_NO)
AND (CLUB_TITLE LIKE '%'||#{keyword}||'%' OR BK_NAME LIKE '%'||#{keyword}||'%')
AND ONOFF_LINE IN
#{item}
AND CATEGORY IN
#{item}
AND TIMES IN
#{item}
ORDER BY CLUB_NO
