mapper.select:

select count(*)
from club_wish
where ref_club_no=#{clubNo} 
and user_id = #{userId}

mapper.update:
UPDATE CLUB 
SET HEART_COUNT = HEART_COUNT+1
WHERE CLUB_NO = #{clubNo}

mapper.delete:
delete from club_wish
where user_id = #{userId}
and ref_club_no in

	#{item}

mapper.update:
UPDATE CLUB_APPLY
SET APPLY_CANCLE = 'Y'
WHERE STATUS='Y' AND
USER_ID=#{userId}
AND REF_TIME_NO=#{timeNo}

	SELECT REF_CLUB_NO
	FROM CLUB_APPLY
	WHERE
	REF_TIME_NO=#{timeNo} AND 
	USER_ID=#{userId}

UPDATE CLUB_TIME
SET APPLY_COUNT = APPLY_COUNT-1
WHERE ${column}=#{columnValue}
